import Table from 'react-bootstrap/Table';
import Container from 'react-bootstrap/Container';
import { useEffect, useImperativeHandle, useState } from 'react';
import { fetchAllUser } from '../services/userService';
import ReactPaginate from 'react-paginate';
// import ModalAddUsers from './ModalAddUsers';
import { forwardRef } from 'react';
import ModalEditUsers from './ModalEditUsers';
import ModalDelete from './ModalDelete';
import _ from 'lodash';


const TableUsers = (props, ref) =>{

    const [listUsers, setListUsers] = useState([])
    // const [totalUsers, setTotalUsers] = useState(0)
    const [totalPages, setTotalPages] = useState(0)

    const [showModalEditUsers, setShowModalEditUsers] = useState(false)
    const [showModalDelete, setShowModalDelete] = useState(false)

    //Lưu thông tin user
    const [dataUserEdit, setDataUserEdit] = useState({})
    const [dataUserDelete, setDataUserDelete] = useState({})

    const [sortBy, setSortBy] = useState("desc")
    const [sortField, setSortField] = useState("asc")


    // const [showModalAddUsers, setShowModalAddUsers] = useState(false)

    // const handleClose = () => {
    //     setShowModalAddUsers(false)
    // }

    // const handleUpdateTable = (user) => {
    //     setListUsers([user, ...listUsers])
    // }

    
    useEffect(()=>{
        //call apis
        getUsers(1)
    }, [])
    
    const getUsers = async (page) =>{
        let res = await fetchAllUser(page)
        
        if (res && res.data){
            // setTotalUsers(res.total)
            setListUsers(res.data)
            setTotalPages(res.total_pages)
        }
    }
    
    const handleClose = () => {
        setShowModalEditUsers(false)
        setShowModalDelete(false)
    }
    
    const handleUpdateTable = (user) => {
        setListUsers([user ,...listUsers])      
    }

    useImperativeHandle(ref, () =>{
        return {
            handleUpdateTable: handleUpdateTable
        }
    })

    const handleEditUser = (user) =>{
        setShowModalEditUsers(true)
        setDataUserEdit(user)
        console.log(user)
    }

    const handleEditTable = (user) =>{
        // let cloneListUsers = [...listUsers]
        let index = listUsers.findIndex(item => item.id === user.id)
        // cloneListUsers[index].first_name = user.first_name
        listUsers[index].first_name = user.first_name
        setListUsers(listUsers)
        // setListUsers(cloneListUsers)       
    }
    
    const handleDelete = (user) =>{
        setShowModalDelete(true)
        setDataUserDelete(user)
        console.log(user)
    }

    const handleDeleteTable = (user) =>{
        const newListUsers = listUsers.filter(item => item.id !== user.id)
        setListUsers(newListUsers)
    }

    const handleSort = (sortBy, sortField) =>{
        setSortBy(sortBy)
        setSortField(sortField)

        let cloneListUsers = _.cloneDeep(listUsers)
        cloneListUsers = _.orderBy(cloneListUsers, [sortField, sortBy])
    }

    const handlePageClick = (ev) =>{
        getUsers(+ev.selected + 1)
    }

    return (<>
        <Container>
            {/* <div className='my-3 list-users'>
                    <span>
                        <h4>List user: </h4>  
                    </span>
                    <button 
                        className='btn btn-success'
                        onClick={() => setShowModalAddUsers(true)}>
                            Add new user
                    </button>
            </div>
            <ModalAddUsers
                show={showModalAddUsers}
                handleClose={handleClose}
                handleUpdateTable={handleUpdateTable}
            /> */}
            <Table striped bordered hover>
            <thead>
                <tr >
                <th className='sort-header'>
                    <span>ID</span>
                    <span>
                        <i 
                            className='fa-solid fa-arrow-down-long'
                            onClick={() => handleSort("desc", "id")}                    
                        ></i>
                        <i 
                            className='fa-solid fa-arrow-up-long'   
                            onClick={() => handleSort("asc", "id")}                                        
                        ></i>
                    </span>
                </th>
                <th >Email</th>
                <th className='sort-header'>
                    <span>First Name</span>
                    <span>
                        <i 
                            className='fa-solid fa-arrow-down-long'
                            onClick={() => handleSort("desc", "first_name")}                    
                        ></i>
                        <i 
                            className='fa-solid fa-arrow-up-long'   
                            onClick={() => handleSort("asc", "first_name")}                                        
                        ></i>
                    </span>
                </th>
                <th >Last Name</th>
                <th >Actions</th>
                </tr>
            </thead>
            <tbody>
                {listUsers && listUsers.length > 0 &&
                    listUsers.map((item, index)=>{
                        return (
                            <tr key={`users-${index}`}>
                            <td>{item.id}</td>
                            <td>{item.email}</td>
                            <td>{item.first_name}</td>
                            <td>{item.last_name}</td>
                            <td>
                                <button 
                                    className='btn btn-warning mx-3'
                                    onClick={() => handleEditUser(item)}
                                >Edit</button>
                                <button 
                                    className='btn btn-danger'
                                    onClick={() => handleDelete(item)}
                                >Delete</button>
                            </td>
                            </tr>
                        )
                    })
                }          
            </tbody>
            </Table>
            <ModalEditUsers
                show={showModalEditUsers}
                handleClose={handleClose}
                handleEditUser={handleEditUser}
                handleEditTable={handleEditTable}
                dataUserEdit={dataUserEdit}
            />
            <ModalDelete
                show={showModalDelete}
                handleClose={handleClose}
                dataUserDelete={dataUserDelete}
                handleDeleteTable={handleDeleteTable}
            />
            <ReactPaginate
                nextLabel="next >"
                onPageChange={handlePageClick}
                pageRangeDisplayed={3}
                marginPagesDisplayed={2}
                pageCount={totalPages}
                previousLabel="< previous"
                pageClassName="page-item"
                pageLinkClassName="page-link"
                previousClassName="page-item"
                previousLinkClassName="page-link"
                nextClassName="page-item"
                nextLinkClassName="page-link"
                breakLabel="..."
                breakClassName="page-item"
                breakLinkClassName="page-link"
                containerClassName="pagination"
                activeClassName="active"
                renderOnZeroPageCount={null}
            />
        </Container>
    </>)
}   

export default forwardRef(TableUsers);
