import { useRef, useState } from 'react';
import Container from 'react-bootstrap/Container';
import ModalAddUsers from './ModalAddUsers';
import TableUsers from './TableUsers';
import { forwardRef } from 'react';

const ListUsers = (props, ref, setListUsers, listUsers) => {

    const [showModalAddUsers, setShowModalAddUsers] = useState(false)

    const handleClose = () => {
        setShowModalAddUsers(false)
    }

    const handleUpdate = () =>{
        setListUsers()
    }

    return (
        <>
            <Container>
                <div className='my-3 list-users'>
                    <span>
                        <h4>List user: </h4>  
                    </span>
                    <button 
                        className='btn btn-success'
                        onClick={() => setShowModalAddUsers(true)}>
                            Add new user
                    </button>
                </div>
            </Container>
            <ModalAddUsers
                show={showModalAddUsers}
                handleClose={handleClose}
                handleUpdate={handleUpdate}
            />
            <TableUsers ref={ref}
            setListUsers={setListUsers}
            listUsers
            />
        </>
    )
}

export default forwardRef(ListUsers)
