import { USER_LOGIN, USER_LOGOUT } from '../actions/userAction'

const INITIAL_STATE = {

    count: { email: '', auth: false }
};

const userReducer = (state = INITIAL_STATE, action) => {

    switch (action.type) {

        case INCREMENT:

            return {

                ...state, count: state.count + 1,

            };

        case DECREMENT:

            return {
                ...state, count: state.count - 1,

            };

            default: return state;

    }

};

export default userReducer;
